/**
 * Icomoon 2 Webkit
 * ---
 * It translate the HTML of ./demo.html (generated by Icomoon) to the html TABLE we need in the Webkit
 * Simply add this two html line at the end of the body tag in demo.html 
 *   <!-- Add this to the end of ./demo-html -->
 *   <script src="../js/jquery-1.12.0.js"></script>
 *   <script src="./icomoon2webkit.js"></script>
 */

(function($){

    $(document).ready(function(){
        var out = '<div class="table-responsive"><table class="table table-hover">'
        +    '<caption class="sr-only">Set di icone del progetto</caption>'
        +    '<thead>'
        +        '<tr>'
        +            '<th>Icona</th>'
        +            '<th>Classe</th>'
        +            '<th>Significato</th>'
        +        '</tr>'
        +    '</thead>'
        +    '<tbody>';


        $('.glyph .mls').each(function(){
            let i = jQuery.trim($(this).html());
            out += '<tr>'
            +   '<td><span class="icon '+i+'"></span></td>'
            +   '<td><code>icon '+i+'</code></td>'
            +   '<td>'+i.substring(2)+'</td>'
            + '</tr>';
        });


        
        out +='</tbody>'
        +'</table></div>';

        $('.glyph').remove();
        $('.mhl').remove();
        $('.bgc1').remove();
        $('body').append(out);


    })
})(jQuery)
